use std::collections::HashMap;
use std::sync::Arc;

use risk_lang::{parse_risk_with_errors, ParseError};
use tokio::sync::Mutex;
use tower_lsp::jsonrpc::Result;
use tower_lsp::lsp_types::{
	CompletionItem, CompletionItemKind, CompletionOptions, CompletionParams, CompletionResponse,
	Diagnostic, DiagnosticSeverity, InitializeParams, InitializeResult, Position, Range, ServerCapabilities,
	TextDocumentSyncCapability, TextDocumentSyncKind,
};
use tower_lsp::{Client, LanguageServer, LspService, Server};

#[derive(Debug)]
struct Backend {
	client: Client,
	documents: Arc<Mutex<HashMap<String, String>>>,
}

impl Backend {
	async fn update_diagnostics(&self, uri: &str, text: &str) {
		let (_, errors) = parse_risk_with_errors(text);
		let diagnostics = errors
			.iter()
			.map(|error| diagnostic_from_error(error, text))
			.collect::<Vec<_>>();
		self.client
			.publish_diagnostics(uri.parse().unwrap(), diagnostics, None)
			.await;
	}
}

#[tower_lsp::async_trait]
impl LanguageServer for Backend {
	async fn initialize(&self, _params: InitializeParams) -> Result<InitializeResult> {
		Ok(InitializeResult {
			capabilities: ServerCapabilities {
				text_document_sync: Some(TextDocumentSyncCapability::Kind(TextDocumentSyncKind::FULL)),
				completion_provider: Some(CompletionOptions {
					resolve_provider: Some(false),
					trigger_characters: Some(vec!["@".to_string()]),
					..CompletionOptions::default()
				}),
				..ServerCapabilities::default()
			},
			..InitializeResult::default()
		})
	}

	async fn initialized(&self, _: tower_lsp::lsp_types::InitializedParams) {
		self.client.log_message(tower_lsp::lsp_types::MessageType::INFO, "risk-lang LSP ready").await;
	}

	async fn shutdown(&self) -> Result<()> {
		Ok(())
	}

	async fn did_open(&self, params: tower_lsp::lsp_types::DidOpenTextDocumentParams) {
		let uri = params.text_document.uri.to_string();
		let text = params.text_document.text;
		self.documents.lock().await.insert(uri.clone(), text.clone());
		self.update_diagnostics(&uri, &text).await;
	}

	async fn did_change(&self, params: tower_lsp::lsp_types::DidChangeTextDocumentParams) {
		let uri = params.text_document.uri.to_string();
		let text = params
			.content_changes
			.into_iter()
			.last()
			.map(|change| change.text)
			.unwrap_or_default();
		self.documents.lock().await.insert(uri.clone(), text.clone());
		self.update_diagnostics(&uri, &text).await;
	}

	async fn completion(&self, params: CompletionParams) -> Result<Option<CompletionResponse>> {
		let uri = params.text_document_position.text_document.uri.to_string();
		let position = params.text_document_position.position;
		let documents = self.documents.lock().await;
		let text = match documents.get(&uri) {
			Some(text) => text,
			None => return Ok(None),
		};

		let (doc, _) = parse_risk_with_errors(text);
		let cursor_line = position.line as usize + 1;
		let actors = actors_defined_before_line(&doc, cursor_line);
		if actors.is_empty() {
			return Ok(None);
		}

		let items = actors
			.into_iter()
			.map(|actor| CompletionItem {
				label: actor.clone(),
				kind: Some(CompletionItemKind::VARIABLE),
				insert_text: Some(actor),
				..CompletionItem::default()
			})
			.collect::<Vec<_>>();

		Ok(Some(CompletionResponse::Array(items)))
	}
}

fn diagnostic_from_error(error: &ParseError, text: &str) -> Diagnostic {
	let zero_line = error.line.saturating_sub(1);
	let zero_col = error.column.saturating_sub(1);
	let line_len = text
		.lines()
		.nth(zero_line)
		.map(|line| line.chars().count())
		.unwrap_or(0);
	let end_col = if line_len == 0 {
		zero_col
	} else {
		std::cmp::min(zero_col + 1, line_len)
	};

	Diagnostic {
		range: Range {
			start: Position {
				line: zero_line as u32,
				character: zero_col as u32,
			},
			end: Position {
				line: zero_line as u32,
				character: end_col as u32,
			},
		},
		severity: Some(DiagnosticSeverity::ERROR),
		message: error.message.clone(),
		..Diagnostic::default()
	}
}

fn actors_defined_before_line(doc: &risk_lang::Document, line: usize) -> Vec<String> {
	let mut actors = Vec::new();
	for declaration in &doc.declarations {
		if let risk_lang::Declaration::ActorDeclaration { actor, location } = declaration {
			let include = match location {
				Some(location) => location.line <= line,
				None => false,
			};
			if include && !actors.contains(actor) {
				actors.push(actor.clone());
			}
		}
	}
	actors
}

#[tokio::main]
async fn main() {
	let (stdin, stdout) = (tokio::io::stdin(), tokio::io::stdout());
	let documents = Arc::new(Mutex::new(HashMap::new()));
	let (service, socket) = LspService::new(|client| Backend { client, documents });
	Server::new(stdin, stdout, socket).serve(service).await;
}

