const cormorant = Cormorant_Garamond({
  subsets: ["latin"],
  weight: ["300", "400", "500", "600", "700"],
  variable: "--font-serif",
});

const inter = Inter({
  subsets: ["latin"],
  weight: ["300", "400", "500", "600", "700"],
  variable: "--font-sans",
});

export const metadata: Metadata = {
  metadataBase: new URL(siteConfig.url),
  title: {
    default: "Enfields - Lovely Homes, Beautifully Shown Off",
    template: "%s | Enfields",
  },
  description: siteConfig.description,
  alternates: {
    canonical: "/",
  },
  openGraph: {
    title: "Enfields - Lovely Homes, Beautifully Shown Off",
    description: siteConfig.description,
    type: "website",
    siteName: siteConfig.name,
    locale: siteConfig.locale,
    url: "/",
    images: [
      {
        url: siteConfig.ogImage,
      },
    ],
  },
  icons: {
    icon: [
      // {
      //   url: "/icon-light-32x32.png",
      //   media: "(prefers-color-scheme: light)",
      // },
      // {
      //   url: "/icon-dark-32x32.png",
      //   media: "(prefers-color-scheme: dark)",
      // },
      {
        url: "/favicon.ico",
        type: "image/svg+xml",
      },
    ],
    apple: "/apple-icon.png",
  },
};

export default async function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const cookieStore = await cookies();
  const hasAccess = cookieStore.get("site_access")?.value === "true";

  return (
    <html lang="en">
      <body
        className={`${inter.variable} ${cormorant.variable} font-sans antialiased`}
      >
        {!hasAccess && <UnderConstructionOverlay />}
        <Header />
        <UnderMaintenanceGate>{children}</UnderMaintenanceGate>
        <Footer />
        <Toaster />
        <Analytics />
      </body>
    </html>
  );
}

