"use client";

import { useState } from "react";
import { validateSitePassword } from "@/app/actions/auth";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import Link from "next/link";
import { ArrowUpRight, Link2Off } from "lucide-react";

export function UnderConstructionOverlay() {
    const [password, setPassword] = useState("");
    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError(false);

        try {
            const result = await validateSitePassword(password);
            if (result.success) {
                window.location.reload();
            } else {
                setError(true);
                setPassword("");
            }
        } catch (err) {
            setError(true);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="fixed inset-0 z-50 bg-primary flex flex-col items-center justify-center p-6">

            <div className="w-full max-w-sm flex flex-col items-center gap-8">
                <div className="text-center space-y-2">
                    <span className="text-xs uppercase tracking-[0.3em] text-muted/40">
                        Restricted Access
                    </span>
                    <h1 className="font-serif text-3xl md:text-4xl text-white">
                        Under Construction
                    </h1>
                </div>

                <form onSubmit={handleSubmit} className="w-full space-y-4">
                    <div className="relative">
                        <Input
                            type="password"
                            inputMode="numeric"
                            pattern="[0-9]*"
                            placeholder="Enter access code"
                            // className="bg-background/40 text-white"
                            value={password}
                            onChange={(e) => {
                                setPassword(e.target.value);
                                setError(false);
                            }}
                            className={`text-center text-background bg-background/10   tracking-widest text-lg h-13 ${error ? "border-red-500 focus-visible:ring-red-500" : ""
                                }`}
                            autoFocus
                        />
                    </div>
                    <Button
                        type="submit"
                        variant="secondary"
                        className="w-full h-14 uppercase tracking-[0.2em] font-medium"
                        disabled={loading || !password}
                    >
                        {loading ? "Verifying..." : "Enter Site"}
                    </Button>
                    <Button variant="outline" asChild className="text-white/80 hover:text-white w-full h-14 uppercase tracking-[0.2em] font-medium">
                        <Link href="https://enfields-southampton.co.uk" target="_blank">
                            Go to Main Website
                            <ArrowUpRight />
                        </Link>
                    </Button>
                </form>
            </div>

            {/* Footer - Matching MaintenanceHero style */}
            <footer className="absolute bottom-0 left-0 right-0 p-6 md:p-12 lg:p-16 flex justify-center">
                <span className="text-xs text-muted-foreground">© {new Date().getFullYear()} Enfields Southampton</span>
            </footer>
        </div>
    );
}

